  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default ({components, ...props}) => <MDXTag name="wrapper"  components={components}><MDXTag name="h1" components={components} props={{"id":"viewlogicjs"}}><MDXTag name="a" components={components} parentName="h1" props={{"aria-hidden":true,"href":"#viewlogicjs"}}><MDXTag name="span" components={components} parentName="a" props={{"className":"icon-link"}}>{`#`}</MDXTag></MDXTag>{`.view.logic.js`}</MDXTag>
<MDXTag name="p" components={components}>{`Any View file can be also wrapped with JavaScript logic to make a Smart View.`}</MDXTag>
<MDXTag name="p" components={components}>{`With `}<MDXTag name="inlineCode" components={components} parentName="p">{`Counter.view`}</MDXTag>{` file like:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre">{`Text
color black
text props.number
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`We can add a `}<MDXTag name="inlineCode" components={components} parentName="p">{`Counter.view.logic.js`}</MDXTag>{` file that would increment a number by one
every second like this:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre">{`import Counter from './Counter.view.js'
import React from 'react'

export default class CounterLogic extends React.Component {
  state = {
    number: 0,
  }

  componentDidMount() {
    this._interval = setInterval(this.increment, 1000)
  }

  componentWillUnmount() {
    clearInterval(this._interval)
  }

  increment = () => {
    this.setState({
      number: this.state.number + 1
    })
  }

  render() {
    return <Counter number={this.state.number} />
  }
}
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`A logic file is an extension to your view and Views imports that it if it's
available. In other words, if you then use your `}<MDXTag name="inlineCode" components={components} parentName="p">{`Counter`}</MDXTag>{` in other views, you
will actually be using the `}<MDXTag name="inlineCode" components={components} parentName="p">{`Counter.view.logic.js`}</MDXTag>{` instead.`}</MDXTag>
<MDXTag name="p" components={components}>{`This is where you would add any intermediate state to your views or connect them
to an external store of data.`}</MDXTag>
<MDXTag name="p" components={components}>{`Reach out with questions via our `}<MDXTag name="a" components={components} parentName="p" props={{"href":"https://slack.viewsdx.com/"}}>{`Slack Questions Channel`}</MDXTag>{`.
Mention `}<MDXTag name="inlineCode" components={components} parentName="p">{`@tom`}</MDXTag>{` or `}<MDXTag name="inlineCode" components={components} parentName="p">{`@dario`}</MDXTag>{` to make sure that we get your notifications.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"control-global-state"}}><MDXTag name="a" components={components} parentName="h2" props={{"aria-hidden":true,"href":"#control-global-state"}}><MDXTag name="span" components={components} parentName="a" props={{"className":"icon-link"}}>{`#`}</MDXTag></MDXTag>{`Control global state`}</MDXTag>
<MDXTag name="p" components={components}>{`Here's the video to the session that shows how to manage global state:`}</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="a" components={components} parentName="p" props={{"href":"https://youtu.be/kUT0crvNhoA"}}><MDXTag name="img" components={components} parentName="a" props={{"src":"../images/ControlGlobalState.png","alt":"Control global state session"}}></MDXTag></MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Here's the link to the `}<MDXTag name="a" components={components} parentName="p" props={{"href":"https://github.com/viewstools/dealing-with-global-app-state"}}>{`repo shown in the video`}</MDXTag>{`.`}</MDXTag></MDXTag>
